import React from 'react';
import { Link } from 'react-router-dom';
import { auth } from '../../firebase/firebase.utils'
import {connect } from 'react-redux';
import {createStructuredSelector} from 'reselect';
import {selectCartHiddden} from '../../redux/cart/cart.selector';
import {selectCurrentUser} from '../../redux/user/user.select';
import CartIcon from '../cart-icon/cart-icon.component';
import CartDrop from '../cart-drop-down/cart-drop-down.component';


import './Header.styles.scss';

const Header = ({CurrentUser,hidden}) => (
    <div className='header'>
            <Link className='home' to='/'>
           HOME
            </Link>
        <div className="options">
            <Link className='option' to='/shop'>
                SHOP
            </Link>
            <Link  className="option" to='/shop'>
                CONTACT
            </Link>
          

            {
    CurrentUser ?
    (
        <div className="option" onClick = {()=>auth.signOut() }
        >SIGN OUT</div>)
        :
     (<Link className="option" to='/signin'>SIGN <span>IN</span> </Link>
)
}
<CartIcon/>
        </div>
{
    hidden ? null :

      (<CartDrop/>  )  
    }
    </div>
)

const mapStateToProps = createStructuredSelector({
    CurrentUser:selectCartHiddden,
    hidden:selectCurrentUser
})

export default connect(mapStateToProps)(Header);
