import React from 'react';
import CustomButton from '../custom-button/custom-button.component';
import CartItems from '../cart-Items/cart-Items.component';
import {connect} from 'react-redux';


import './cart-drop-down.styles.scss';

const CartDrop = ( {addCart}) =>(
    <div className="cart-dropdown">
        <div   className="cart-items" >
            {
        addCart.map(CartItems =>(
        <CartItems key={CartItems.id} item={CartItems}/> )  )
            }
        </div>
        <CustomButton className='btn'>GO TO CHECK OUT</CustomButton>
    </div>
);


const mapStateToProps = ({cart : {addCart}}) =>({
addCart
})
export default connect(mapStateToProps)(CartDrop);
