const AddItemToCart = (addCart, AddCartItem) =>{
 const   existingItem = addCart.find(
        cartAdd => cartAdd.id == AddCartItem.id
    )

    if (existingItem){
return addCart.map(cartAdd => cartAdd.id =  AddCartItem.id ?
        {...cartAdd, quantity: cartAdd.quantity + 1}
        : cartAdd )

    }
    return [...addCart , {...AddCartItem, quantity:1}]
} 

export default AddItemToCart;
