import React from 'react';
import {Switch, Route } from 'react-router-dom';
import { connect } from 'react-redux'

import './App.css';


import HomePage from './pages/homepage/homepage.component';
import ShopPage from './pages/shoPage/shopPage.component';
import Header from './component/Header/Header.component';
import SignInUp from './pages/sign-in-sign-out/signIn-signOut.component';
import  {fire} from './firebase/firebase.utils';
import currentUser from './redux/user/user.action';

class App extends React.Component{
constructor(props){
  super(props);
  this.state = {
    User: {},
  }
}


componentDidMount(){
  this.authListener();
}

authListener(){
  fire.auth().onAuthStateChanged((User) =>{
    if (User) {
    this.setState({User})
    }else {
      this.setState({User:null});
    }
      })
}



componentWillUnmount(){
  this.unsubcribeUser();
}

render(){
  return (


    <div>

  <Header/>

   <Switch>
<Route exact path='/' component = {HomePage} />
<Route path='/shop' component = {ShopPage} />
<Route  path='/Signin' component={SignInUp}/>

   </Switch>

</div>
    )
}

}

const mapDispatchToProps = currentUser =>({
  currentUser: user =>currentUser (Dispatch(user))
});

export default connect(mapDispatchToProps)(App);
