import React from 'react';
import { Switch, Route } from 'react-router-dom';
import './App.css';

import HomePage from './pages/homepage/homepage.component';
import ShopPage from './pages/shoPage/shopPage.component';
import Header from './component/Header/Header.component';
import SignInUp from './pages/sign-in-sign-out/signIn-signOut.component';
import {auth , CreaUserProfileDocument} from './firebase/firebase.utils';



class App extends React.Component{
  constructor(){
    super();
    this.state = {
      CurrentUser: null
    }
  }

  unsubscribeAuth = null

 componentDidMount(){
   this.unsubscribeAuth = auth.onAuthStateChanged(async userAuth =>{
     if (userAuth){
    const userRef = await CreaUserProfileDocument(async);
    userRef.onsnapShot(snapShot=>{
      this.setState({
        CurrentUser :{
          id: snapShot.id,
          ...snapShot.data()
        }
      })
    })
     }
this.setState({CurrentUser: userAuth })
   })
 }


  componentWillUnmount(){
    this.unsubscribeAuth();
  }

render(){
  return (
    <div>
      <Header CurrentUser = {this.state.CurrentUser}/>
     <Switch> 

  <Route exact path='/' component={HomePage} />
  <Route path='/shop' component={ShopPage} />
  <Route path='/signin' component={SignInUp} />

  </Switch>
    </div>
    )
}

}

export default App;
