import CartActionTypes from './cart.type';

const ToggleHiddenCart = ()=>({
    type:CartActionTypes.TOGGLE_HIDDEN_CART
})

export const AddItem = item=({
    type:CartActionTypes.CART_ADD_ITEMS,
    payload:item
})

export default ToggleHiddenCart  ;
