import React from 'react';
import {Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux'

import './App.css';


import HomePage from './pages/homepage/homepage.component';
import ShopPage from './pages/shoPage/shopPage.component';
import Header from './component/Header/Header.component';
import SignInUp from './pages/sign-in-sign-out/signIn-signOut.component';
import  {fire} from './firebase/firebase.utils';
import {currentUser} from './redux/user/user.action';



class App extends React.Component{


componentDidMount(){


  this.authListener();
}

authListener(){
  const {currentUser} = this.props;

  fire.auth().onAuthStateChanged((User) =>{
    if (User) {
     currentUser({User})
    }else {
     currentUser({User:null});
    }
      })
}



componentWillUnmount(){
  this.unsubcribeUser();
}

render(){
  return (


    <div>

  <Header/>

   <Switch>
<Route exact path='/' component = {HomePage} />
<Route path='/shop' component = {ShopPage} />
<Route exact path='/Signin' render = {()=> this.props.User? 
(<Redirect to='/' />) : <SignInUp/> }/>

   </Switch>    

</div>
    )
}

}

const mapStateToProps = ({user}) => ({
  User : user.User
})

const mapDispatchToProps = Dispatch =>({
  currentUser: user =>Dispatch (currentUser(user))
});

export default connect(
  mapStateToProps,
  mapDispatchToProps)(App);
