}

render(){
  return (


    <div>

      <Header/>
<HomePage/>
      <Switch>
      <Route exact path='/' Component={HomePage}/>
      <Route  path='/SignInUp' Component={SignInUp}/>
      <Route path='/ShopPage' Component={ShopPage}/>

      </Switch>


    </div>
    )
}

}

export default App;
